-- title: star trip
-- author: visy
-- desc: now with manual control

t=0
x=96
y=24

stars = {}
starcount = 1024

cam = {
	x = 0,
	y = 0
}

min_z = 15
max_z = 50

angle = 0
angle_c = 0
zoom = 5
xo = -128
yo = -64
speed = 0.000

function draw_space()
	cls(0)
end

function draw_stars()

	table.sort(stars, function(a, b) return a.z > b.z end)

	for i=1,starcount do
		local s = stars[i]
		
		s.z=s.z % 512

		local proj_z = s.z / max_z
		
		local pos = {
			x = (128-s.xrot) / proj_z,
			y = (64-s.yrot) / proj_z
		}

		local size = s.size / proj_z
		stars[i].p_size = size
		elli(128+pos.x - size / 2, 64+pos.y - size / 2, size, size,16-s.z/32)
	end
end

function draw_ui()
	ss = math.floor(math.abs(speed*1000*68)/1000)
	if speed >= 0 then
 rect(0,68-ss,8,ss,9)
	else
 rect(0,68,8,ss,2)
 end
 rectb(0,0,8,136,2)
 rectb(1,1,6,134,3)
end

function draw()
	draw_space()
	draw_stars()
	draw_ui()
end

function init_star()
	local s = {
	 size=3+math.random()*3,
		x=math.random()*320,
		y=math.random()*128,
		z=32*math.random() * (max_z - min_z) + min_z
	}
	return s
end

function init()
	for i=1,starcount do
		s = init_star()
		table.insert(stars,s)
	end
end

function update_stars()
	for i=1,starcount do
		local s = stars[i]

		local sin = math.sin(angle)*zoom
		local cos = math.cos(angle)*zoom

		s.xrot = ((xo+s.x)*cos - (yo+s.y)*sin)
		s.yrot = ((yo+s.y)*cos + (xo+s.x)*sin)
	
		s.xrot = s.xrot-xo	
		s.yrot = s.yrot-yo	
		s.z = s.z-speed*dt
		if (s.z < 0) then
		 s = init_star()
			s.xrot = ((xo+s.x)*cos - (yo+s.y)*sin)
			s.yrot = ((yo+s.y)*cos + (xo+s.x)*sin)
		
			s.xrot = s.xrot-xo	
			s.yrot = s.yrot-yo	
			stars[i] = s
		end
	end
end

function update_input()
	if btn(0) then speed=speed+dt*0.0004 end
	if btn(1) then speed=speed-dt*0.0004 end

	if btn(2) then angle_c=angle_c+dt*0.00005 end
	if btn(3) then angle_c=angle_c-dt*0.00005 end

	if btn(4) then speed=0 end
	if btn(5) then angle_c = 0 end
	
	if speed > 1 then speed = 1 end
	if speed < -1 then speed = -1 end

	if angle_c < -1 then angle_c = -1 end
	if angle_c > 1 then angle_c = 1 end

	zoom = 15-10*math.abs(speed)

	angle=angle+angle_c

end

function update()
	update_stars()
	update_input()
end

init()

pt = 0
dt = 0
function TIC()
	dt = time()-pt
	pt = time()
	update()
	draw()
end

